mapper.select:
SELECT t.`id`, t.`userid`, t.`username`, t.`roleid`, t.`rolename`, t.`systemid`, t.`processid`, p.`name` AS processname, t.`totaltask`, t.`edittask`, t.`qctask`, t.`checktask`, t.`completetask`, t.`time` FROM tb_worktasks t LEFT JOIN tb_process p ON p.`id` = t.`processid`
t.`time` = (SELECT MAX(`time`) FROM tb_worktasks
WHERE `time` < #{time} )
AND t.`userid` = #{userid}
AND t.`username` like '%${username}%'
AND t.`roleid` = #{roleid}
AND t.`processid` = #{processid}
AND p.`name` like #{processname}
ORDER BY
${orderby},
t.`userid`, t.`roleid`
LIMIT #{limit}
OFFSET #{offset}

SELECT
	COUNT(1)
FROM
	tb_worktasks t
	LEFT JOIN tb_process p ON p.`id` = t.`processid`

	t.`time` = (SELECT MAX(`time`) FROM tb_worktasks
	
		WHERE `time` < #{time}
	 )
	
		AND t.`userid` = #{userid}
	
	
		AND t.`username` like '%${username}%'
	
	
		AND t.`roleid` = #{roleid}
	
	
		AND t.`processid` = #{processid}
	
	
		AND p.`name` like #{processname}

SELECT p.`id` AS projectid, p.`name` AS projectname, p.`processid` AS processid, p.`systemid`, p.`tasknum` AS totaltask, SUM(CASE WHEN t.`roleid` = 5 THEN t.`edittask` ELSE 0 END) AS edittask, SUM(CASE WHEN t.`roleid` = 5 THEN t.`qctask` ELSE 0 END) AS qctask, SUM(CASE WHEN t.`roleid` = 5 THEN t.`checktask` ELSE 0 END) AS checktask, SUM(CASE WHEN t.`roleid` = 5 THEN t.`completetask` ELSE 0 END) AS completetask, p.`tasknum` - SUM(CASE WHEN t.`roleid` = 5 THEN t.`edittask` ELSE 0 END) - SUM(CASE WHEN t.`roleid` = 5 THEN t.`qctask` ELSE 0 END) - SUM(CASE WHEN t.`roleid` = 5 THEN t.`checktask` ELSE 0 END) - SUM(CASE WHEN t.`roleid` = 5 THEN t.`completetask` ELSE 0 END) AS idletask FROM projectmanager.tb_projects p LEFT JOIN process.tb_worktasks t ON t.`projectid` = p.`id`
p.`id` = #{projectid}
AND (p.`owner` = 0 or (p.`owner` = 1 and p.`id` in
#{projectID}
))
AND p.`name` like #{projectname}
AND p.`processid` = #{processid}
GROUP BY p.`id` ORDER BY
${orderby},
p.`priority` desc, p.`id`
LIMIT #{limit}
OFFSET #{offset}
